mapper.select:
    	select p.paramcode, p.paramvalue
		  from CMS_jobplanparam p
		 where 1 = 1
		   and p.jobcode = #{jobCode}
		   and p.jobplancode = #{jobPlanCode}

select t.jobName,t.startDate,t.endDate,t.runState,t.runResult,t.makeDate,t.makeUser,t.modifyDate,t.modifyUser from (select v.*, rownum as rn from ( SELECT A.JOBNAME, to_char(B.STARTDATE,'yyyy-mm-dd hh24:mi:ss') STARTDATE, to_char(B.ENDDATE,'yyyy-mm-dd hh24:mi:ss') ENDDATE, (case when B.RUNSTATE='0' then '执行中' when B.RUNSTATE='1' then '已完成' else '' end) RUNSTATE, B.RUNRESULT, to_char(B.MAKEDATE,'yyyy-mm-dd hh24:mi:ss') MAKEDATE, B.MAKEUSER, to_char(B.MODIFYDATE,'yyyy-mm-dd hh24:mi:ss') MODIFYDATE, B.MODIFYUSER FROM CMS_JOBDEF A, CMS_JOBRUNLOG B WHERE A.JOBCODE = B.JOBCODE
AND B.JOBCODE = #{jobCode}
AND B.JOBCODE=(SELECT JOBCODE FROM CMS_JOBDEF WHERE JOBNAME=#{jobName})
AND TO_DATE(to_char(B.STARTDATE,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') >= TO_DATE(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND TO_DATE(to_char(B.ENDDATE,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') <= TO_DATE(#{endDate},'yyyy-mm-dd hh24:mi:ss')
AND B.RUNSTATE = #{runState}
ORDER BY B.SERIALNO DESC ) v where rownum <= #{pageEnd}) t where t.rn > #{pageStart}

SELECT count(B.SERIALNO) FROM CMS_JOBDEF A, CMS_JOBRUNLOG B WHERE A.JOBCODE = B.JOBCODE
AND B.JOBCODE = #{jobCode}
AND B.JOBCODE=(SELECT JOBCODE FROM CMS_JOBDEF WHERE JOBNAME=#{jobName})
AND TO_DATE(to_char(B.STARTDATE,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') >= TO_DATE(#{startDate},'yyyy-mm-dd hh24:mi:ss')
AND TO_DATE(to_char(B.ENDDATE,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') <= TO_DATE(#{endDate},'yyyy-mm-dd hh24:mi:ss')
AND B.RUNSTATE = #{runState}
