mapper.select:
SELECT CMS_JOBRUNLOG.SERIALNO as key ,cms_jobdef.JOBNAME,to_char(CMS_JOBRUNLOG.STARTDATE,'YYYY-MM-DD HH24:MI:SS') as startDate,to_char(CMS_JOBRUNLOG.ENDDATE,'YYYY-MM-DD HH24:MI:SS') as endDate,CMS_JOBRUNLOG.RUNSTATE, CMS_JOBRUNLOG.RUNRESULT,to_char(CMS_JOBRUNLOG.MAKEDATE,'YYYY-MM-DD HH24:MI:SS') as makeDate,CMS_JOBRUNLOG.MAKEUSER,to_char(CMS_JOBRUNLOG.MODIFYDATE,'YYYY-MM-DD HH24:MI:SS') as modifyDate,CMS_JOBRUNLOG.MODIFYUSER FROM CMS_JOBRUNLOG,cms_jobdef where CMS_JOBRUNLOG.JOBCODE=CMS_JOBDEF.JOBCODE
AND CMS_JOBDEF.JOBCODE=#{jobCode}
AND CMS_JOBRUNLOG.RUNSTATE=#{runstate}
AND CMS_JOBRUNLOG.ENDDATE <= to_date(#{endDate},'YYYY-MM-DD HH24:MI:SS')
AND CMS_JOBRUNLOG.STARTDATE >= to_date(#{startDate},'YYYY-MM-DD HH24:MI:SS') order by CMS_JOBRUNLOG.STARTDATE desc
